mapper.update:
update fly_user
attempts=#{attempts}, login_ip=#{loginIp}, last_login=#{lastLogin}, attempts_time=#{attemptsTime},
where user_id = #{userId}

update fly_user
user_name = #{userName}, password = #{password}, user_email = #{userEmail}, true_name = #{trueName}, nick_name = #{nickName}, sex = #{sex}, birthday = #{birthday}, signature = #{signature}, work = #{work}, description = #{description}, telephone = #{telephone}, user_mobile = #{userMobile}, province = #{province}, city = #{city}, area = #{area}, contact_addr = #{contactAddr}, zip = #{zip}, qq = #{qq}, status = #{status},
where user_id = #{userId}

update fly_user_account



			score = score+#{score}


			score = score-#{score}



where user_id=#{userId}

update fly_user_group_merge
group_id=#{groupId}, serve_start_time=#{serveStartTime}, serve_end_time=#{serveEndTime},
where user_id=#{userId}

update fly_user_session
session_key=#{sessionKey}, expire_time=#{expireTime}, update_time=#{updateTime},
where user_id = #{userId}

mapper.select:
select count(*) from fly_user

	and user_id != #{userId}
	and user_name=#{userName}

select count(*) from fly_user

	and user_id != #{userId}
	and (user_mobile=#{userMobile} or user_name=#{userMobile})

select count(*) from fly_user

	and user_id != #{userId}
	and user_email=#{userEmail}

select count(*) from fly_user

	and user_id != #{userId}
	and nick_name=#{nickName}

select count(1) from fly_user

	and user_name = #{userName}
	and true_name = #{trueName}
	and user_mobile = #{userMobile}
	and user_email = #{userEmail}

		select * from fly_user

			and user_name = #{userName}
			and true_name = #{trueName}
			and user_mobile = #{userMobile}
			and user_email = #{userEmail}

        order by
        ${orderby}
        ${order}
		limit #{offset},#{rows}

select count(*) from fly_user_fans

    and user_follow = #{userFollow}
    and user_fans = #{userFans}
    and DATE(create_time) = #{createTime}

select * from fly_user_fans

    and user_follow = #{userFollow}
    and user_fans = #{userFans}
    and DATE(create_time) = #{createTime}

order by
${orderby}
${order}
limit #{offset},#{rows}

select count(*) from fly_user u
inner join fly_user_count c on u.user_id=c.user_id
inner join fly_user_account a on u.user_id=a.user_id

    and u.user_name = #{userName}
    and u.nick_name = #{nickName}
    and u.user_mobile = #{userMobile}
    and u.user_email = #{userEmail}
    and u.province = #{province}
    and u.city = #{city}
    and u.area = #{area}
    and u.status >= 0
    and u.status = 0
    and u.status = 1
    and u.status = 2
    and u.status = 3
    and u.nick_name is not null

select u.user_id from fly_user u
inner join fly_user_count c on u.user_id=c.user_id
inner join fly_user_account a on u.user_id=a.user_id

    and u.user_name = #{userName}
    and u.nick_name = #{nickName}
    and u.user_mobile = #{userMobile}
    and u.user_email = #{userEmail}
    and u.province = #{province}
    and u.city = #{city}
    and u.area = #{area}
    and u.status >= 0
    and u.status = 0
    and u.status = 1
    and u.status = 2
    and u.status = 3
    and u.nick_name is not null

order by
${orderby}
${order}
limit #{offset},#{rows}
